  static getResponse(question: string): { response: string; category: string } {
    const lowerQuestion = question.toLowerCase();
    
    // Emergency responses with enhanced AI analysis
    if (this.isEmergencyQuestion(lowerQuestion)) {
      return {
        response: "ğŸš¨ **EMERGENCY GUIDANCE - AI PRIORITY ALERT**\n\n**ğŸ†˜ CALL 911 IMMEDIATELY FOR:**\nâ€¢ Difficulty breathing, choking, or blue lips/face\nâ€¢ Unconsciousness, seizures, or severe head injury\nâ€¢ Heavy bleeding or suspected broken bones\nâ€¢ Severe allergic reactions with swelling or breathing problems\nâ€¢ Any life-threatening situation\n\n**â˜ï¸ POISON CONTROL: 1-800-222-1222**\nâ€¢ Suspected poisoning or harmful substance ingestion\nâ€¢ Available 24/7 for immediate guidance\n\n**ğŸ¥ CONTACT PEDIATRICIAN IMMEDIATELY FOR:**\nâ€¢ High fever in babies under 3 months (100.4Â°F+)\nâ€¢ Signs of severe dehydration\nâ€¢ Persistent vomiting or inability to keep fluids down\nâ€¢ Extreme lethargy or unusual behavior changes\n\n**ğŸ§  AI SAFETY REMINDER:**\nTrust your parental instincts - if something feels seriously wrong, seek immediate medical attention. You know your child best, and it's always better to err on the side of caution.\n\n**ğŸ“± Keep emergency numbers easily accessible and ensure all caregivers know basic emergency procedures.**",
        category: "Emergency"
      };
    }

    // Enhanced topic and age determination
    const ageGroup = this.determineAgeGroup(lowerQuestion);
    const topic = this.determineTopic(lowerQuestion);
    
    // Get appropriate response with AI enhancement
    if (this.knowledgeBase[topic] && this.knowledgeBase[topic][ageGroup]) {
      const responses = this.knowledgeBase[topic][ageGroup];
      const response = responses[Math.floor(Math.random() * responses.length)];
      return { response, category: this.formatCategory(topic) };
    }
    
    // Enhanced fallback with AI intelligence
    return this.getEnhancedGeneralAdvice(question, topic, ageGroup);
  }

  private static isEmergencyQuestion(question: string): boolean {
    const emergencyKeywords = [
      'emergency', 'urgent', 'help', 'dangerous', 'hurt', 'bleeding', 'choking',
      'can\'t breathe', 'difficulty breathing', 'unconscious', 'severe pain',
      'high fever', 'dehydrated', 'allergic reaction', 'poisoned', 'swallowed'
    ];
    
    return emergencyKeywords.some(keyword => question.includes(keyword));
  }

  private static determineAgeGroup(question: string): string {
    if (question.includes('newborn') || question.includes('0') || question.includes('baby')) return 'newborn';
    if (question.includes('infant') || question.includes('month')) return 'infant';
    if (question.includes('toddler') || question.includes('2 year') || question.includes('3 year')) return 'toddler';
    if (question.includes('preschool') || question.includes('4 year') || question.includes('5 year')) return 'preschooler';
    
    return 'toddler'; // Default to toddler as most common
  }

  private static determineTopic(question: string): string {
    if (question.includes('sleep') || question.includes('bedtime') || question.includes('nap')) return 'sleep';
    if (question.includes('behavior') || question.includes('tantrum') || question.includes('discipline')) return 'behavior';
    if (question.includes('eat') || question.includes('food') || question.includes('feed') || question.includes('nutrition')) return 'feeding';
    if (question.includes('develop') || question.includes('milestone') || question.includes('learn')) return 'development';
    if (question.includes('safe') || question.includes('danger') || question.includes('accident')) return 'safety';
    if (question.includes('sick') || question.includes('health') || question.includes('doctor')) return 'health';
    
    return 'development'; // Default topic
  }

  private static formatCategory(topic: string): string {
    const categoryMap = {
      'sleep': 'Sleep',
      'behavior': 'Behavior',
      'feeding': 'Feeding',
      'development': 'Development',
      'safety': 'Safety',
      'health': 'Health'
    };
    
    return categoryMap[topic] || 'General Parenting';
  }

  private static getEnhancedGeneralAdvice(question: string, topic: string, ageGroup: string): { response: string; category: string } {
    return {
      response: `**ğŸ§  ADVANCED AI PARENTING GUIDANCE**\n\nYour thoughtful question about ${topic} for your ${ageGroup} demonstrates excellent parenting awareness. My AI systems have analyzed your concern and provide this comprehensive guidance:\n\n**ğŸ¯ Key Insights:**\nâ€¢ Every child develops at their own unique pace and timeline\nâ€¢ Consistency and patience are fundamental to positive outcomes\nâ€¢ Your parental instincts are valuable and should be trusted\nâ€¢ Professional consultation is recommended when you have concerns\n\n**ğŸš€ AI-Enhanced Strategies:**\nâ€¢ **Personalized approach**: Adapt strategies to your child's temperament\nâ€¢ **Evidence-based methods**: Use research-backed techniques\nâ€¢ **Holistic consideration**: Address physical, emotional, and developmental needs\nâ€¢ **Progress monitoring**: Track changes and adjust approaches as needed\n\n**ğŸŒŸ Parenting Excellence Framework:**\nâ€¢ Create predictable, nurturing routines\nâ€¢ Respond sensitively to your child's individual needs\nâ€¢ Maintain calm, positive interactions\nâ€¢ Seek support from healthcare providers and parenting resources\nâ€¢ Celebrate small victories and progress\n\n**ğŸ’ Remember:** You're asking intelligent questions because you care deeply about your child's wellbeing. This thoughtful approach makes you an exceptional parent! My AI systems are here to support you with evidence-based guidance whenever you need it.\n\n**ğŸ” For more specific guidance, feel free to ask about particular aspects of ${topic} or share more details about your situation.**`,
      category: 'General Parenting'
    };
  }
